local a,b=...local c=tonumber(table.remove(a,1))local d={}local e=1;if b.h==true or b.help==true then print("-----------------------------------------------")print("-- Extensible Application Framework - Manual --")print("-----------------------------------------------")print("  >> NAME")print("    >> xaf list - XAF built-in command for printing available command list")print()print("  >> SYNOPSIS")print("    >> xaf list")print("    >> xaf list [-h | --help]")print()print("  >> DESCRIPTION")print("    >> This command is mainly used for getting more information about commands and keywords which allows controlling and managing the XAF package.")os.exit()end;if c==nil then c=1 else if c>e then c=e elseif c<1 then c=1 else c=math.floor(c)end end;print("---------------------------------------------------")print("-- Extensible Application Framework - Controller --")print("---------------------------------------------------")print("  >> XAF controller command list, printed page "..c.." from "..e.." pages")print("  >> Use command with '-h' or '--help' option for its manual")d[1]={"    >> xaf check - Tries to check for potential updates.","    >> xaf init - Initializes XAF package API. Always use before other commands.","    >> xaf list - Prints this command list.","    >> xaf release - Retrieves XAF release data and prints it.","    >> xaf remove - Uninstalls the XAF package with its API and configuration.","    >> xaf update - Downloads XAF package in specified version and automatically installs it."}for f,g in ipairs(d[c])do print(g)end
